mapper.update:
update course
name=#{name}, course_hour=#{courseHour}, course_credit=#{courseCredit}, course_year=#{courseYear}, semester=#{semester}, type=#{type}, teach_no=#{teachNo},
WHERE course_no = #{courseNo}

update course
attendance_percent=#{attendancePercent}, work_percent=#{workPercent}, experiment_percent=#{experimentPercent}, other_percent=#{otherPercent},
WHERE course_no = #{courseNo}
